mapper.select:
select
from sys_menu a inner join sys_role_menu b on a.id=b.menu_id
a.del_flag='0' and b.role_id = #{roleId}

select
from sys_menu a inner join sys_role_menu b on a.id=b.menu_id inner join sys_user_role c on c.role_id=b.role_id
a.del_flag='0' and exists ( select 1 from sys_role r where r.del_flag='0' and r.id=b.role_id and r.status = '1' ) and a.type in (1, 2) and c.user_id = #{userId}
